mapper.select:
		select
		
		from account_special_food
		where 1=1
		  
			and id = #{id,jdbcType=INTEGER}
		
		  
			and store_id = #{storeId,jdbcType=VARCHAR}
		
		  
			and store_name = #{storeName,jdbcType=VARCHAR}
		
		  
			and special_offer_food_name = #{specialOfferFoodName,jdbcType=VARCHAR}
		
		  
			and create_date = #{createDate,jdbcType=TIMESTAMP}
		
		  
			and sale_num = #{saleNum,jdbcType=INTEGER}
		
		  
			and orgin_price = #{orginPrice,jdbcType=DECIMAL}
		
		  
			and settlement_price = #{settlementPrice,jdbcType=DECIMAL}
		
		  
			and special_offer_orgin_price = #{specialOfferOrginPrice,jdbcType=DECIMAL}
		
		  
			and special_offer_settlement = #{specialOfferSettlement,jdbcType=DECIMAL}
		
		  
			and platform_type = #{platformType,jdbcType=VARCHAR}
		
				  
           	order by ${orderFields} ${order}

mapper.update:
update account_special_food  set
id= #{id,jdbcType=INTEGER}
,store_id = #{storeId,jdbcType=VARCHAR}
,store_name = #{storeName,jdbcType=VARCHAR}
,special_offer_food_name = #{specialOfferFoodName,jdbcType=VARCHAR}
,create_date = #{createDate,jdbcType=TIMESTAMP}
,sale_num = #{saleNum,jdbcType=INTEGER}
,orgin_price = #{orginPrice,jdbcType=DECIMAL}
,settlement_price = #{settlementPrice,jdbcType=DECIMAL}
,special_offer_orgin_price = #{specialOfferOrginPrice,jdbcType=DECIMAL}
,special_offer_settlement = #{specialOfferSettlement,jdbcType=DECIMAL}
,platform_type = #{platformType,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
