mapper.select:
		select
		
		from dn_store_activity
		where 1=1
		  
			and STORE_ACTIVITY_ID = #{storeActivityId,jdbcType=CHAR}
		
		  
			and STORE_ID = #{storeId,jdbcType=CHAR}
		
		  
			and ACTIVITY_ID = #{activityId,jdbcType=CHAR}
		
		  
			and ACTIVITY_TIME_BEGIN = #{activityTimeBegin,jdbcType=TIMESTAMP}
		
		  
			and ACTIVITY_TIME_END = #{activityTimeEnd,jdbcType=TIMESTAMP}
		
		  
			and ACTIVITY_EXPECTED_BUDGET = #{activityExpectedBudget,jdbcType=DOUBLE}
		
		  
			and ACTIVITY_EXPECTED_RETURN = #{activityExpectedReturn,jdbcType=DOUBLE}
		
		  
			and ACTIVITY_STATUS = #{activityStatus,jdbcType=CHAR}
		
		  
			and ACTIVITY_ACTUAL_PROFIT = #{activityActualProfit,jdbcType=DOUBLE}
		
		  
			and ACTIVITY_EXECUTOR = #{activityExecutor,jdbcType=VARCHAR}
		
		  
			and ACTIVITY_EXECUTE_TIME = #{activityExecuteTime,jdbcType=TIMESTAMP}
		
				  
           	order by ${orderFields} ${order}

mapper.update:
update dn_store_activity  set
ID= #{storeActivityId,jdbcType=CHAR}
											,STORE_ID = #{storeId,jdbcType=CHAR}
									,ACTIVITY_ID = #{activityId,jdbcType=CHAR}
									,ACTIVITY_TIME_BEGIN = #{activityTimeBegin,jdbcType=TIMESTAMP}
									,ACTIVITY_TIME_END = #{activityTimeEnd,jdbcType=TIMESTAMP}
									,ACTIVITY_EXPECTED_BUDGET = #{activityExpectedBudget,jdbcType=DOUBLE}
									,ACTIVITY_EXPECTED_RETURN = #{activityExpectedReturn,jdbcType=DOUBLE}
									,ACTIVITY_STATUS = #{activityStatus,jdbcType=CHAR}
									,ACTIVITY_ACTUAL_PROFIT = #{activityActualProfit,jdbcType=DOUBLE}
									,ACTIVITY_EXECUTOR = #{activityExecutor,jdbcType=VARCHAR}
									,ACTIVITY_EXECUTE_TIME = #{activityExecuteTime,jdbcType=TIMESTAMP}
					where STORE_ACTIVITY_ID = #{storeActivityId,jdbcType=CHAR}
