mapper.select:
		select
		
		from dn_store
		where 1=1
		  
			and STORE_ID = #{storeId,jdbcType=CHAR}
		
		  
			and STORE_NAME = #{storeName,jdbcType=VARCHAR}
		
		  
			and BRAND_ID = #{brandId,jdbcType=CHAR}
		
		  
			and STORE_ADDRESS = #{storeAddress,jdbcType=VARCHAR}
		
		  
			and WORK_TIME_BEGIN = #{workTimeBegin,jdbcType=TIMESTAMP}
		
		  
			and WORK_TIME_END = #{workTimeEnd,jdbcType=TIMESTAMP}
		
		  
			and STORE_OWNER_NAME = #{storeOwnerName,jdbcType=VARCHAR}
		
		  
			and STORE_OWNER_TEL = #{storeOwnerTel,jdbcType=VARCHAR}
		
		  
			and PRO_INVOICE_FLAG = #{proInvoiceFlag,jdbcType=CHAR}
		
		  
			and REMARK = #{remark,jdbcType=VARCHAR}
		
		  
			and OPERATE_DATE = #{operateDate,jdbcType=DATE}
		
		  
			and SETTLEMENT_METHOD = #{settlementMethod,jdbcType=CHAR}
		
		  
			and STORE_TEL = #{storeTel,jdbcType=VARCHAR}
		
		  
			and REAL_IMAGE_PATH_1 = #{realImagePath1,jdbcType=VARCHAR}
		
		  
			and REAL_IMAGE_PATH_2 = #{realImagePath2,jdbcType=VARCHAR}
		
		  
			and REAL_IMAGE_PATH_3 = #{realImagePath3,jdbcType=VARCHAR}
		
				  
           	order by ${orderFields} ${order}

mapper.update:
update dn_store  set
ID= #{storeId,jdbcType=CHAR}
											,STORE_NAME = #{storeName,jdbcType=VARCHAR}
									,BRAND_ID = #{brandId,jdbcType=CHAR}
									,STORE_ADDRESS = #{storeAddress,jdbcType=VARCHAR}
									,WORK_TIME_BEGIN = #{workTimeBegin,jdbcType=TIMESTAMP}
									,WORK_TIME_END = #{workTimeEnd,jdbcType=TIMESTAMP}
									,STORE_OWNER_NAME = #{storeOwnerName,jdbcType=VARCHAR}
									,STORE_OWNER_TEL = #{storeOwnerTel,jdbcType=VARCHAR}
									,PRO_INVOICE_FLAG = #{proInvoiceFlag,jdbcType=CHAR}
									,REMARK = #{remark,jdbcType=VARCHAR}
									,OPERATE_DATE = #{operateDate,jdbcType=DATE}
									,SETTLEMENT_METHOD = #{settlementMethod,jdbcType=CHAR}
									,STORE_TEL = #{storeTel,jdbcType=VARCHAR}
									,REAL_IMAGE_PATH_1 = #{realImagePath1,jdbcType=VARCHAR}
									,REAL_IMAGE_PATH_2 = #{realImagePath2,jdbcType=VARCHAR}
									,REAL_IMAGE_PATH_3 = #{realImagePath3,jdbcType=VARCHAR}
					where STORE_ID = #{storeId,jdbcType=CHAR}
